import { buildNuxt } from '@nuxt/kit';
import { r as resolve, a as relative } from '../shared/nuxi-edge.a03f3b10.mjs';
import { c as consola } from '../shared/nuxi-edge.5f813239.mjs';
import { c as clearDir } from '../shared/nuxi-edge.3196727a.mjs';
import { l as loadKit } from '../shared/nuxi-edge.b94eae5e.mjs';
import { w as writeTypes } from '../shared/nuxi-edge.9202ce8d.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi-edge.f39aafd9.mjs';
import '../shared/nuxi-edge.76bb3f1e.mjs';
import 'util';
import 'path';
import 'fs';
import 'os';
import 'tty';
import 'node:fs';
import 'node:module';
import 'node:url';

const prepare = defineNuxtCommand({
  meta: {
    name: "prepare",
    usage: "npx nuxi prepare",
    description: "Prepare nuxt for development/build"
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({ rootDir, config: { _prepare: true } });
    await clearDir(nuxt.options.buildDir);
    await buildNuxt(nuxt);
    await writeTypes(nuxt);
    consola.success("Types generated in", relative(process.cwd(), nuxt.options.buildDir));
  }
});

export { prepare as default };
